<?php	function show_js($file)	{		global $root;		$path = $root.'/'.$file.'.js';		$md5 = filemtime($path);		$cpath = 'resources/'.$md5.'.js';		if(!file_exists($cpath))		{			require_once('JSMin.php');			$min = new JSMin();			$js = $min->minify(file_get_contents($path));			file_put_contents($cpath, $js);		}		echo '<script type="text/javascript" src="/'.$cpath.'"></script>'."\n";	}	function show_css($file)	{		global $root;		$path = $root.'/'.$file.'.css';		$md5 = filemtime($path);		$cpath = 'resources/'.$md5.'.css';		if(!file_exists($cpath))		{			require_once('CSSMin.php');			$js = CSSMin::minify(file_get_contents($path));			file_put_contents($cpath, $js);		}		echo '<link rel="stylesheet" href="/'.$cpath.'" />'."\n";	}?>